    @Override
    public String processMatch(String match) {
        if (salt==null)
        {
            throw new IllegalArgumentException("Salt cannot be null");
        }

        // We found an email so lets hash it

        int leadSpaceCount = 0;

        while (Character.isWhitespace(match.charAt(leadSpaceCount)))
        {
            leadSpaceCount++;
        }

        String realMatch = match.substring(leadSpaceCount, match.length());
        int atIdx = realMatch.indexOf("@");

        return match.substring(0,leadSpaceCount)+ DigestUtils.shaHex((salt + realMatch.substring(0, atIdx)).getBytes())+realMatch.substring(atIdx);
    }

